import { getFirestore } from "firebase/firestore/lite";
import { Link } from "react-router-dom";
import firebase from '../firebase';

function InsertarFeeling() {
    console.log(firebase);
    async function insertar(){
        let db=getFirestore();
        await setDoc(doc(db, "correos", "111"), {
            datos:"1"
        });
    }

    return (
        <div className="App">
            <header>
                <nav  class="navbar navbar-light bg-light  ">
                    <div class="container-fluid " style={{justifyContent:"center"}}>
                        <span class="navbar-brand mb-0 h1 " >¿Cómo te sientes hoy?</span>
                    </div>
                </nav>
            </header>
            <div style={{textAlign:"left"}} >
                <label for="feeling">¿De que Lugar nos visitas?</label><br/>
                <input id="feeling" placeholder="Lugar" maxLength={20}></input>
            </div>
            <br/>
            <div style={{textAlign:"left"}} >
                <label for="feeling">Escribe el sentimiento que mejor te describa</label><br/>
                <input id="feeling" placeholder="Sentimiento" maxLength={20}></input>
            </div>
            <br/>
            <div style={{textAlign:"left"}} >
                <label for="feeling">Descripción</label><br/>
                <input id="feeling" style={{width:'20%'}} placeholder="Los demas tienen que saberlo" ></input>
            </div>

            <div class=''>
                <div style={{paddingBottom:10}}>
                    <button class="btn btn-secondary" onClick={()=>{
                        insertar();
                    }} >Actualizar</button>
                </div>
                <div>
                    <Link  className="btn btn-secondary" to="/">Regresar</Link>

                </div>
                
            </div>

        </div>
    );
}

export default InsertarFeeling;
